import React, { useContext, useState } from "react";
import FetchData from "../Components/FetchData";
import LangContext from "../Context/LangContext";
import CoinCalculator from "../Components/CoinCalculator";
import Contact from "../Components/Contact";
import { getData } from "../components/data";

export async function getStaticProps() {
  const allData = getData();
  return {
    props: {
      allData,
    },
  };
}

export default function Home({ allData }) {
  const ref = React.useRef();
  const [metal, setMetal] = useState(0);
  const [sek, setSek] = useState(0);
  const context = useContext(LangContext);

  function parentFunction(metal, sek) {
    setMetal(metal);
    setSek(sek);
  }
  return (
    <div>
      {/* <FetchData functionCallFromParent={parentFunction} /> */}
      <div className="wrap">
        {context.lang === "sv" ? (
          <h1>Aktuella myntpriser!</h1>
        ) : (
          <h1>Current coin prices!</h1>
        )}

        <CoinCalculator metal={allData.metal} sek={allData.sek} />
        <Contact passedRef={ref} />
      </div>
    </div>
  );
}
