import React, { useContext } from "react";
import Image from "next/image";
import arrowImg from "/Arrow.png";
import LangContext from "../Context/LangContext";
import styled from "styled-components";

const StyledContact = styled.div`
  background-color: #292929;
  width: 100%;
  height: auto;
  color: white;
  display: flex;
  flex-direction: column;
  padding-bottom: 5px;

  h2 {
    padding: 30px;
  }

  p {
    padding-left: 30px;
  }

  Button {
    background-color: #292929;
    color: $colorDark;
    padding: 5px 10px;
    align-self: center;
  }
`;

function scroll(props) {
  window.scrollTo({
    behavior: "smooth",
    top: props.passedRef.current.offsetTop,
  });
}

export default function Contact(props) {
  const context = useContext(LangContext);

  return (
    <StyledContact>
      {context.lang === "sv" ? <h2>Kontakta oss</h2> : <h2>Contact us</h2>}
      {context.lang === "sv" ? <p>Adress:</p> : <p>Adress:</p>}
      {context.lang === "sv" ? <p>Telefon:</p> : <p>Phone:</p>}
      {context.lang === "sv" ? <p>E-post:</p> : <p>Email:</p>}

      <button onClick={() => scroll(props)}>
        <img src={arrowImg} alt="arrow" width="60"></img>
      </button>
    </StyledContact>
  );
}
