import React, { useContext } from "react";
import LangContext from "../Context/LangContext";
import CoinCalculator from "../Components/CoinCalculator";
import Contact from "../Components/Contact";
//import { getData } from "./api/data";
import useSwr from "swr";

const fetcher = (url) => fetch(url).then((res) => res.json());
/* 
export async function getStaticProps() {
  const allData = await getMetalData();
  return {
    props: {
      allData,
    },
  };
} */

function getMetalData() {
  const { data, error } = useSwr(
    "https://metals-api.com/api/latest?access_key=qba30a3bp2c9dl6oupq250f11my19te3u5fkbd6jk807o831mnb1g1o5jaiz&base=EUR&symbols=XAG",
    fetcher
  );

  return data;
}

function getSekData() {
  const { data, error } = useSwr(
    "https://api.exchangeratesapi.io/latest?base=EUR",
    fetcher
  );

  return data;
}

export default function Home() {
  // const ref = React.useRef();
  const context = useContext(LangContext);

  //const { metalData, error } = useSwr('https://metals-api.com/api/latest?access_key=qba30a3bp2c9dl6oupq250f11my19te3u5fkbd6jk807o831mnb1g1o5jaiz&base=EUR&symbols=XAG', fetcher)

  if (!getSekData() && !getMetalData()) return <div>Loading...</div>;
  return (
    <div>
      <div className="wrap">
        {context.lang === "sv" ? (
          <h1>Aktuella myntpriser!</h1>
        ) : (
          <h1>Current coin prices!</h1>
        )}
        <CoinCalculator metal={getMetalData()} sek={getSekData()} />
        {/* <Contact passedRef={ref} /> */}
      </div>
    </div>
  );
}
